  const raw = payload.raw ?? {};
  const toObj = (v) => (v && typeof v === "object" && !Array.isArray(v)) ? v : {};
  const toArr = (v) => Array.isArray(v) ? v : [];
  const canonical = toObj(payload.canonical);
  const optionMap = toObj(payload.optionMap);

  const cleanName = (s) => (s ?? "").toString().trim();
  const cleanVal  = (s) => (s ?? "").toString().trim();

  const isRealValue = (v) => {
    const x = cleanVal(v);
    if (!x) return false;
    if (/^default title$/i.test(x)) return false;
    return true;
  };

  let rawOptions = toArr(raw.options)
    .map(o => ({
      name: cleanName(o?.name),
      position: Number(o?.position ?? 0),
      values: toArr(o?.values).map(cleanVal).filter(isRealValue),
      id: o?.id,
    }))
    .filter(o => o.name);

  const rawVariants = toArr(raw.variants);

  if (rawOptions.length === 0 && rawVariants.length > 0) {
    const inferred = [];
    for (let i = 1; i <= 3; i++) {
      const key = `option${i}`;
      const vals = rawVariants
        .map(v => v?.[key])
        .map(cleanVal)
        .filter(isRealValue);

      if (vals.length) {
        inferred.push({
          name: `Option ${i}`,
          position: i,
          values: [...new Set(vals)],
        });
      }
    }
    rawOptions = inferred;
  }

  const canonicalOptions = rawOptions
    .sort((a,b)=>a.position-b.position)
    .map((o, idx) => {
      const canonName = o.name;
      if (!optionMap[canonName]) optionMap[canonName] = o.name;

      return {
        name: canonName,
        position: idx + 1,
        values: o.values,
      };
    });

  const canonVariants = rawVariants.map(v => {
    const optionValues = [];
    const optionsObj = {};

    for (let i = 0; i < canonicalOptions.length; i++) {
      const pos = i + 1;
      const canonName  = canonicalOptions[i].name;
      const val = cleanVal(v?.[`option${pos}`]);
      if (isRealValue(val)) {
        optionValues.push(val);
        optionsObj[canonName] = val;
      }
    }

    return {
      id: v?.id,
      sku: v?.sku ?? null,
      price: v?.price ?? null,
      compare_at_price: v?.compare_at_price ?? null,
      inventory_quantity: v?.inventory_quantity ?? null,
      options: optionsObj,
      optionValues,
    };
  });

  canonical.options  = canonicalOptions;
  canonical.variants = canonVariants;
  canonical.optionNames = canonicalOptions.map(o => o.name);

  nextJson = {
    ...payload,
    canonical,
    optionMap,
  };
} catch (error) {
  recordError(status, stageName, error.message, { node: 'Normalize Options to Canonical', stack: error.stack });
  payload.patch = payload.patch || {};
  payload.patch.skip = true;
  payload.patch.reason = payload.patch.reason || 'Normalize Options failed';
  nextJson = { ...payload };
} finally {
  finalizeStage(status, stageName, startedAt);
}

nextJson.status = status;
