    }
    if (!payload.patch?.product) {
      payload.patch = { skip: true, reason: 'No patch.product to sanitize' };
      recordError(status, stageName, 'Patch payload missing product', { node: 'Build Patch' });
      continue;
    }

    const raw = payload.raw || {};
    const ai  = payload.ai || {};
    const loc = payload.localized || {};
    const minimal = payload.patch.product;

    const pt =
      (raw.product_type || raw.productType ||
       loc.product_type || ai.product_type || '')
        .toString().toLowerCase();

    const apparel =
      payload.canonical?.isApparel === true ||
      /(dress|shirt|tee|hoodie|pants|jeans|skirt|jacket|coat|sweater|top|blouse|apparel|clothing)/i.test(pt);

    let body = minimal.body_html || '';
    if (apparel) {
