// This Function takes rows extracted from the OptionMap file
// and builds two dictionaries:
//
// 1) canonicalByRaw: raw_string (any language) → Canonical key
// 2) localesByCanonical: Canonical key → translations in all languages
//
// This allows us to:
// - Normalize ANY color/option string into a canonical form
// - Then output it in ANY target language dynamically (via targetLang)

const rows = $input.all().map(i => i.json);

const canonicalByRaw = {};
const localesByCanonical = {};

const langColumns = [
  "US English",
  "UK English",
  "Italian",
  "French",
  "German",
  "Swedish",
  "Danish",
];

for (const row of rows) {
  const canonical = (row["Canonical"] || "").trim();
  const type = (row["Type"] || "").trim().toLowerCase();

  if (!canonical || !type) continue;

  // Build the locales structure
  localesByCanonical[canonical] = {};

  for (const col of langColumns) {
    const val = (row[col] || "").trim();
    localesByCanonical[canonical][col] = val;
  }

  // Reverse index: ANY raw value → canonical
  for (const col of langColumns) {
    const rawVal = (row[col] || "").trim();
    if (!rawVal) continue;

    canonicalByRaw[rawVal.toLowerCase()] = canonical;
  }
}

return [
  {
    json: {
      canonicalByRaw,
      localesByCanonical,
    }
  }
];

// Build Option Map (robust)
// Mode: Run Once for All Items

const rows = $input.all().map(i => i.json);

// 0) Если на вход уже пришла готовая optionMap — просто возвращаем как есть
if (
  rows.length === 1 &&
  rows[0] &&
  rows[0].canonicalByRaw &&
  rows[0].localesByCanonical
) {
  return [{ json: rows[0] }];
}

// helper: найти колонку по regex (case-insensitive)
function findCol(obj, regexList) {
  const keys = Object.keys(obj || {});
  for (const rx of regexList) {
    const k = keys.find(x => rx.test(x));
    if (k) return k;
  }
  return null;
}

const canonicalByRaw = {};
const localesByCanonical = {};

// возможные варианты названий колонок
const canonicalRegex = [/^canonical$/i, /canonical/i, /kanon/i];
const typeRegex      = [/^type$/i, /type/i, /gruppe/i, /kategorie/i];

// языковые колонки — ищем по известным названиям, но тоже case-insensitive
const langColumns = [
  "US English",
  "UK English",
  "Italian",
  "French",
  "German",
  "Swedish",
  "Danish",
];

// 1) определяем реальные имена колонок по первой строке
const sample = rows[0] || {};
const canonicalCol = findCol(sample, canonicalRegex);
const typeCol = findCol(sample, typeRegex);

// если не нашли — вернём пустые карты, но с понятной ошибкой
if (!canonicalCol || !typeCol) {
  return [{
    json: {
      canonicalByRaw: {},
      localesByCanonical: {},
      error: true,
      message: `Columns not found. canonicalCol=${canonicalCol}, typeCol=${typeCol}`,
      sampleKeys: Object.keys(sample),
    }
  }];
}

for (const row of rows) {
  const canonical = (row[canonicalCol] || "").toString().trim();
  const type = (row[typeCol] || "").toString().trim().toLowerCase();
  if (!canonical || !type) continue;

  if (!localesByCanonical[canonical]) {
    localesByCanonical[canonical] = { type };
  }

  for (const col of langColumns) {
    // ищем колонку языка тоже без учёта регистра
    const realLangCol =
      Object.keys(row).find(k => k.toLowerCase() === col.toLowerCase()) || col;

    const val = (row[realLangCol] || "").toString().trim();
    localesByCanonical[canonical][col] = val;

    if (val) {
      canonicalByRaw[val.toLowerCase()] = canonical;
    }
  }
}

return [{
  json: {
    canonicalByRaw,
    localesByCanonical,
  }
}];

// Code node: Normalize Options to Canonical
// Mode: Run Once for Each Item

const j = $json ?? {};
const raw = j.raw ?? {};

const toObj = (v) => (v && typeof v === "object" && !Array.isArray(v)) ? v : {};
const toArr = (v) => Array.isArray(v) ? v : [];

const canonical = toObj(j.canonical);
const optionMap = toObj(j.optionMap);

// --- helpers ---
const cleanName = (s) => (s ?? "").toString().trim();
const cleanVal  = (s) => (s ?? "").toString().trim();

const isRealValue = (v) => {
  const x = cleanVal(v);
  if (!x) return false;
  if (/^default title$/i.test(x)) return false;
  return true;
};

// 1) Get raw options, if any
let rawOptions = toArr(raw.options)
  .map(o => ({
    name: cleanName(o?.name),
    position: Number(o?.position ?? 0),
    values: toArr(o?.values).map(cleanVal).filter(isRealValue),
    id: o?.id
  }))
  .filter(o => o.name);

// 2) If raw.options missing or empty, infer from variants option1..option3
const rawVariants = toArr(raw.variants);

if (rawOptions.length === 0 && rawVariants.length > 0) {
  // infer option count by scanning variants.option1..3 presence
  const inferred = [];
  for (let i = 1; i <= 3; i++) {
    const key = `option${i}`;
    const vals = rawVariants
      .map(v => v?.[key])
      .map(cleanVal)
      .filter(isRealValue);

    if (vals.length) {
      inferred.push({
        name: `Option ${i}`,
        position: i,
        values: [...new Set(vals)]
      });
    }
  }
  rawOptions = inferred;
}

// 3) Canonicalize option names minimally (keep raw as canonical unless you later remap)
// You can plug your own Canonicalize Options rules later.
const canonicalOptions = rawOptions
  .sort((a,b)=>a.position-b.position)
  .map((o, idx) => {
    const canonName = o.name; // Phase 1: 1:1
    if (!optionMap[canonName]) optionMap[canonName] = o.name;

    return {
      name: canonName,
      position: idx + 1,
      values: o.values
    };
  });

// 4) Rebuild canonical variants with normalized option objects
const canonVariants = rawVariants.map(v => {
  const optionValues = [];
  const optionsObj = {};

  for (let i = 0; i < canonicalOptions.length; i++) {
    const pos = i + 1;
    const rawOptName = rawOptions[i]?.name;
    const canonName  = canonicalOptions[i].name;

    const val = cleanVal(v?.[`option${pos}`]);

    // accept empty but don't add junk
    if (isRealValue(val)) {
      optionValues.push(val);
      optionsObj[canonName] = val;
    }
  }

  return {
    id: v?.id,
    sku: v?.sku ?? null,
    price: v?.price ?? null,
    compare_at_price: v?.compare_at_price ?? null,
    inventory_quantity: v?.inventory_quantity ?? null,
    options: optionsObj,
    optionValues
  };
});

// 5) Write back
canonical.options  = canonicalOptions;
canonical.variants = canonVariants;

// Also store a quick lookup set for later steps
canonical.optionNames = canonicalOptions.map(o => o.name);

return {
  json: {
    ...j,
    canonical,
    optionMap
  }
};

// Code node: Init Layers
// Mode: Run Once for Each Item

const j = $json ?? {};

// 1) Resolve raw product source safely
// Priority: explicit raw -> explicit product -> full json
const rawSource =
  (j.raw && typeof j.raw === "object")  ? j.raw :
  (j.product && typeof j.product === "object") ? j.product :
  j;

// 2) Merge/normalize layers from any nesting level
const canonical = (j.canonical ?? rawSource.canonical ?? {});
const ai        = (j.ai ?? rawSource.ai ?? {});
const localized = (j.localized ?? rawSource.localized ?? {});
const patch     = (j.patch ?? rawSource.patch ?? {});

// Ensure layers are plain objects (not arrays/null/primitives)
const toObj = (v) => (v && typeof v === "object" && !Array.isArray(v)) ? v : {};
const safeCanonical = toObj(canonical);
const safeAi        = toObj(ai);
const safeLocalized = toObj(localized);
const safePatch     = toObj(patch);

// 3) Carry optionMap/config from anywhere, keep objects
const optionMap = toObj(j.optionMap ?? rawSource.optionMap ?? {});
const config    = toObj(j.config    ?? rawSource.config    ?? {});

// 4) Detect sourceType reliably
// If Shopify product: has admin_graphql_api_id OR variants array typical of Shopify payload
const isShopify =
  !!rawSource.admin_graphql_api_id ||
  (Array.isArray(rawSource.variants) && rawSource.variants.length >= 0);

const sourceType = j.sourceType ?? (isShopify ? "shopify" : "competitor");

// 5) Return layered structure
return {
  json: {
    raw: rawSource,
    canonical: safeCanonical,
    ai: safeAi,
    localized: safeLocalized,
    patch: safePatch,
    optionMap,
    config,
    sourceType,
  }
};

// Build Patch: sanitize placeholders + wrap for Shopify REST
const items = $input.all();

function stripPlaceholders(html, mode) {
  if (!html) return "";
  let out = html;

  if (mode === "size") {
    out = out.replace(/<!--\s*SIZE-CHART-PLACEHOLDER\s*-->\s*/gi, "");
  }
  if (mode === "dims") {
    out = out.replace(/<!--\s*DIMENSIONS-PLACEHOLDER\s*-->\s*/gi, "");
  }
  return out.trim();
}

for (const item of items) {
  const j = item.json;

  if (j.patch?.skip) continue;
  if (!j.patch?.product) {
    j.patch = { skip: true, reason: "No patch.product to sanitize" };
    continue;
  }

  const raw = j.raw || {};
  const ai  = j.ai || {};
  const loc = j.localized || {};
  const minimal = j.patch.product;

  // ✅ Robust apparel detector: raw -> localized -> ai
  const pt =
    (raw.product_type || raw.productType ||
     loc.product_type || ai.product_type || "")
      .toString().toLowerCase();

  const apparel =
    j.canonical?.isApparel === true ||
    /(dress|shirt|tee|hoodie|pants|jeans|skirt|jacket|coat|sweater|top|blouse|apparel|clothing)/i.test(pt);

  let body = minimal.body_html || "";

  if (apparel) {
    body = stripPlaceholders(body, "dims");  // keep SIZE, remove DIMS
  } else {
    body = stripPlaceholders(body, "size");  // keep DIMS, remove SIZE
  }

  minimal.body_html = body;

  // final REST envelope for Shopify PUT /products/{id}.json
  j.patch.product = {
    product: {
      id: minimal.id,
      title: minimal.title,
      handle: minimal.handle,
      body_html: minimal.body_html,
      tags: minimal.tags,
      product_type: minimal.product_type,
    }
  };

  // safety: no options/variants in patch
  delete j.patch.product.product.options;
  delete j.patch.product.product.variants;
}

return items;

// Map AI -> localized (+ patch draft) + productId
const items = $input.all();

for (const item of items) {
  const j = item.json;

  j.localized = j.localized || {};
  j.patch = j.patch || {};

  // If validator said skip OR no ai => skip downstream
  if (j.aiValidation?.ok === false || j.patch.skip || !j.ai || !j.ai.title) {
    j.patch = { skip: true, reason: j.patch.reason || "AI validation failed" };
    continue;
  }

  const ai = j.ai;
  const raw = j.raw || {};

  // reliable productId for URL
  const productId = raw.id || ai.shopifyProductId || ai.id;
  if (!productId) {
    j.patch = { skip: true, reason: "No productId found in raw/ai" };
    continue;
  }
  j.productId = productId;

  // localized.product — store final localized values
  j.localized = {
    shopifyProductId: productId,
    title: ai.title,
    handle: ai.handle,
    body_html: ai.body_html,
    tags: ai.tags,
    product_type: ai.product_type,
  };

  // patch.product — MINIMAL fields only (no envelope yet)
  j.patch = {
    product: {
      id: productId,
      title: ai.title,
      handle: ai.handle,
      body_html: ai.body_html,
      tags: ai.tags,
      product_type: ai.product_type,
    }
  };
}

return items;

// Code node before AI: Build aiInput
const r = $json.raw;
const c = $json.canonical;

const aiInput = {
  shopifyProductId: r.id,   // <-- добавили
  id: r.id,                 // можно оставить для совместимости
  title: r.title,
  handle: r.handle,
  vendor: r.vendor,
  product_type: r.product_type,
  tags: r.tags,
  body_html: r.body_html,
  options: (r.options || []).map(o => ({ name: o.name, values: o.values })),
  images: (r.images || []).slice(0, 2).map(i => ({ src: i.src, alt: i.alt })),
  canonical: {
    options: c.options,
    optionNames: c.optionNames
  }
};

return {
  json: {
    ...$json,
    aiInput
  }
};

const items = $input.all();

for (const item of items) {
  const j = item.json;

  const raw = j.raw || {};
  const ai  = j.ai  || {};
  const loc = j.localized || {};

  // --- где лежит реальный AI output ---
  // В твоём флоу AI-нода возвращает:
  // item.json.message.content
  // MapAI потом кладёт это в j.ai и j.localized
  //
  // Поэтому тут берем максимально устойчиво:
  const aiOut =
    loc.shopifyProductId ? loc :
    ai.shopifyProductId  ? ai  :
    (j.message && j.message.content) ? j.message.content :
    null;

  j.aiValidation = { ok: true, errors: [], warnings: [] };

  if (!aiOut || typeof aiOut !== "object") {
    j.aiValidation.ok = false;
    j.aiValidation.errors.push("AI output missing or not an object");
    j.patch = { skip: true, reason: "AI validation failed" };
    continue;
  }

  // --- productId из входа (из aiInput) ---
  const inputId =
    (j.aiInput && (j.aiInput.shopifyProductId || j.aiInput.id)) ||
    raw.id ||
    j.shopifyProductId;

  if (inputId && aiOut.shopifyProductId && Number(aiOut.shopifyProductId) !== Number(inputId)) {
    j.aiValidation.ok = false;
    j.aiValidation.errors.push(
      `shopifyProductId mismatch (ai=${aiOut.shopifyProductId}, input=${inputId})`
    );
  }

  // --- проверки body_html по шаблону ---
  const body = (aiOut.body_html || "").toString();

  const hasKeyBenefits = body.includes("<h3>Key Benefits</h3>");
  if (!hasKeyBenefits) {
    j.aiValidation.ok = false;
    j.aiValidation.errors.push("body_html missing heading: <h3>Key Benefits</h3>");
  }

  // Проверяем, что второй H3 один из допустимых
  const secondH3Allowed = [
    "<h3>Details</h3>",
    "<h3>The Fit & Feel</h3>",
    "<h3>Design Highlights</h3>"
  ].some(h => body.includes(h));

  if (!secondH3Allowed) {
    j.aiValidation.ok = false;
    j.aiValidation.errors.push("body_html missing valid second heading");
  }

  // --- placeholder rules ---
  const hasSize = body.includes("<!-- SIZE-CHART-PLACEHOLDER -->");
  const hasDim  = body.includes("<!-- DIMENSIONS-PLACEHOLDER -->");

  // здесь мягкая проверка: не валим, только предупреждаем
  if (!hasSize && !hasDim) {
    j.aiValidation.warnings.push("No placeholder marker included");
  }

  // если не ок — ставим skip
  if (!j.aiValidation.ok) {
    j.patch = { skip: true, reason: "AI validation failed" };
  } else {
    // кладем нормализованный aiOut в j.ai для следующих нод
    j.ai = aiOut;
  }
}

return items;
